create table tb_cart (id int8 generated by default as identity, moment timestamp, products_id int8, vouchers_id int8, primary key (id));
create table tb_category (id int8 generated by default as identity, description varchar(255), name varchar(255), primary key (id));
create table tb_product (id int8 generated by default as identity, description varchar(255), name varchar(255), price varchar(255), category_id int8, primary key (id));
create table tb_voucher (id int8 generated by default as identity, description varchar(255), discount_amount varchar(255), name varchar(255), primary key (id));
alter table if exists tb_cart add constraint FKa2sf9quupfb7m0wk6qml45907 foreign key (products_id) references tb_product;
alter table if exists tb_cart add constraint FKi6rdre83t6jcn2mw1njt0ml3y foreign key (vouchers_id) references tb_voucher;
alter table if exists tb_product add constraint FK8i0sq9mfbpsrabrm2pum9fspo foreign key (category_id) references tb_category;
